INSERT INTO `tbl_news_detail` (`IdNews`, `Title`, `Discreption`, `Content`, `UrlImage`, `IdNewsGroup`) VALUES
(1, '12 Thực phẩm để ăn vào buổi sáng tốt nhất cho sức khỏe của bạn\r\n', 'Một bữa ăn sáng chất lượng và lành mạng là 1 điều rất quan trọng và rất cần thiết cho cơ thể bạn phục hồi sau 1 đêm 8 tiếng cơ thể bạn bị bỏ đói. Dưới đây là một số thực phẩm mà dinh dưỡng thể hình xin mách nhỏ cho các bạn, những thực phẩm lành mạnh và chất lượng nhất cho 1 bữa ăn sáng\r\n', '', 'new1.jpg', '1'),
(2, 'Vitamine và khoáng chất cần thiết nhất cho quá trình giảm cân của bạn', 'Nếu bạn muốn giảm cân bằng cách thay đổi chế độ ăn uống, hãy nhớ đảm bảo chế độ dinh dưỡng bằng cách cung cấp cho cơ thể 6 loại vitamin và khoáng chất cần thiết sau.', '', 'new2.jpg', '1'),
(3, 'Sự thật về lời đồn BCAA giúp giảm mỡ cơ thể', 'Các sản phẩm BCAAs là các sản phẩm thực phẩm bổ sung có hiệu quả trong khi giảm béo hoặc giảm cân. Chúng có thể giúp bạn đạt được kết quả tốt hơn nhanh hơn bằng cách giúp bạn duy trì khối lượng cơ trong khi bạn giảm mỡ cơ thể. Với chế độ ăn uống và tập luyện kết hợp với bổ sung BCAA nó có thế giúp bạn có thân hình thon gọn ', '', 'new3.jpg', '1'),
(4, 'Glucosamine là gì ? Tại sao nó là quan trọng nhất cho Gymer', 'Glucosamine đóng một vai trò quan trọng trong việc xây dựng sụn, và nhiều người dùng nó như một chất bổ sung để điều trị viêm khớp và viêm xương khớp. Nó xuất hiện tự nhiên trong chất lỏng xung quanh khớp, trong xương động vật, tủy xương, động vật có vỏ và nấm. Glucosamine, đặc biệt là glucosamine sulfate, được chiết xuất từ ​​vỏ ', '', 'new4.jpg', '1'),
(5, 'Khám phá ra CrossFit là gì đối với sức khỏe thể hình', 'Trong các loại hình thể thao để áp dụng trong quá trình tập thể hình thì Crossfit luôn được đề cập đến nhiều nhất. Không những là cách thức xây dựng cơ bắp và thân hình được hoàn hảo mà còn là loại hình dễ sử dụng nhất. Chính vì thế mà không quá khó để tìm hiểu về Crossfit là gì và sự kết hợp loại hình này trong quá trình ', '', 'new5.png', '2'),
(6, 'Squat là gì? Cách tập Squat như thế nào là đúng kỹ thuật?', 'là động tác được biết đến từ lâu đời và là động tác nổi tiếng nhất trong tập thể hình, nó được gọi là vua của các bài tập tạ. Nó nổi tiếng là vì tác dụng cực tốt mà nó đem lại cho bạn', '', 'new6.jpg', '2'),
(7, 'Chi tiết lịch tập gym cho người mới tập', 'Đối với người mới lần đầu bước chân tới phòng tập gym, có rất nhiều thắc mắc không biết hỏi ai, làm thế nào! Và làm sao để có một chương trình tập luyện phù hợp với người mới tập là điều cực kỳ khó khăn.', '', 'new7.jpg', '2'),
(8, 'Hướng dẫn lich tập gym giảm mỡ bụng cho nữ\r\n', 'Hướng dẫn lich tập gym giảm mỡ bụng Những hoạt động thể dục, thể thao, tập GYM l&agrave; một trong những c&aacute;ch thức để c&aacute;c bạn nữ giảm mỡ bụng. B&agrave;i viết dưới đ&acirc;y sẽ giới thiệu những b&agrave;i tập GYM giảm mỡ bụng cho nữ, gi&uacute;p cho c&aacute;c chị em giảm mỡ bụng th&agrave;nh c&ocirc;ng, nhanh ch&oacute;ng sở hữu một v&ograve;ng eo thon gọn, săn\r\n', '', 'new8.jpg', '2');

